sp_widget.client_script:
api.controller = function($scope, spUtil, $uibModal, $rootScope) {
    /* widget controller */
    var c = this;

    $scope.Next = function() {
        c.data.displayInfo = '';
        c.data.gender = "";
        c.data.plural = "";
        c.data.example1 = "";
        c.data.example2 = "";
        c.server.update();

    };

    $scope.Info = function() {
        c.data.displayInfo = 'yes';
        c.data.w = c.data.word;
        c.data.s = c.data.speech;
        c.server.update();


    };
};

