sp_widget.client_script:
api.controller = function($scope, spUtil,$uibModal,$rootScope) {
    /* widget controller */
    var c = this;
c.data.text = '';
c.data.commentsField = {
        "isVisible": function(){return true;},
        "sys_mandatory": false,
        "visible": true,
        "color": "transparent",
        "dbType": -1,
        "label": "Comments",
        "sys_readonly": false,
        "type": "string",
        "mandatory": false,
        "displayValue": "",
        "readonly": false,
        "hint": "",
        "name": "comments",
        "attributes": {
            "edge_encryption_enabled": "true"
        },
        "choice": 0,
        "value": "",
        "max_length": 4000,
        "ed": {
            "name": "comments"
        },
        "originalValue": ""
    };


c.data.newAss = '';

    $scope.assignment = {


        displayValue: c.data.assignment,


        value: c.data.assignmentID,


        name: 'assignment'


    };
    $scope.$on("field.change", function(evt, parms) {

        if (parms.field.name == 'assignment') {
		    c.data.assignment = parms.displayValue;
            c.data.newAss = $scope.assignment.value;
            c.server.update();
        }

    });







    c.mychoiceoptions = [{
            myChoiceDisplay: "New",
            myChoiceValue: '1'
        },
        {
            myChoiceDisplay: "In Progress",
            myChoiceValue: '2'
        },
        {
            myChoiceDisplay: "Ready for review",
            myChoiceValue: '3'
        }
    ];

    c.mychoicelistoption = {
        hideSearch: true
    };

    c.mychoice = c.data.stateNr;


    c.state = {


        label: c.data.state,


        value: c.data.stateNr,


        name: 'state'


    };

    c.state = c.data.stateNr;
	$scope.clickMethod = function () {
  if(confirm("Are you sure you want to update the record?")) {
    console.log("Implement save functionality here");
  }
}
};

